<node CREATED="1299143165114" FOLDED="true" ID="ID_1088536286" MODIFIED="1318329313321" TEXT="inf">
<node CREATED="1299143137038" ID="ID_1593388172" MODIFIED="1299143157751">
<richcontent TYPE="NODE"><html>
  <head>
    
  </head>
  <body>
    <p>
      &#160;The casts allowed are:
    </p>
    <ul class="itemizedlist">
      <li class="listitem">
        <span class="simpara">(int), (integer) - cast to </span><a href="http://www.php.net/manual/en/language.types.integer.php" class="type integer"><span class="type">integer</span></a>
      </li>
      <li class="listitem">
        <span class="simpara">(bool), (boolean) - cast to </span><a href="http://www.php.net/manual/en/language.types.boolean.php" class="type boolean"><span class="type">boolean</span></a>
      </li>
      <li class="listitem">
        <span class="simpara">(float), (double), (real) - cast to </span><a href="http://www.php.net/manual/en/language.types.float.php" class="type float"><span class="type">float</span></a>
      </li>
      <li class="listitem">
        <span class="simpara">(string) - cast to </span><a href="http://www.php.net/manual/en/language.types.string.php" class="type string"><span class="type">string</span></a>
      </li>
      <li class="listitem">
        <span class="simpara">(array) - cast to </span><a href="http://www.php.net/manual/en/language.types.array.php" class="type array"><span class="type">array</span></a>
      </li>
      <li class="listitem">
        <span class="simpara">(object) - cast to </span><a href="http://www.php.net/manual/en/language.types.object.php" class="type object"><span class="type">object</span></a>
      </li>
      <li class="listitem">
        <span class="simpara">(unset) - cast to </span><a href="http://www.php.net/manual/en/language.types.null.php" class="type NULL"><span class="type">NULL</span></a><span class="simpara">&#160;(PHP 5)</span>
      </li>
    </ul>
  </body>
</html></richcontent>
</node>
</node>
